import { StyleSheet, Text, View, Button } from 'react-native'
import React , {useState } from 'react'
import Icon from 'react-native-vector-icons/FontAwesome';
import { Input } from 'react-native-elements';
import DatePicker from "react-native-datepicker";
// import ImagePicker from 'react-native-image-picker'

// import MenuDrawer from '../routes/MenuDrawer';


const AddEquipment = ({navigation})=>{
    localStorage.setItem("subscription", true);
    const [date, setDate] = useState(new Date());
    const [image, setImage] = useState(null);
    // navigation.navigate('MenuDrawer')
    return (
        <View style={styles.container}>
            <View style = {styles.Input} >
            <View style={styles.datePicker}>
                <Text>Equipment Name : </Text>
                <Input placeholder='Equipment Name' leftIcon={{ type: 'font-awesome', name: 'id-badgeconstruct-outline' }}/>
            </View>
            <View style={styles.datePicker}>
                <Text>Equipment Specifications : </Text>
                <Input
                    placeholder='Equipment Specifications'
                    leftIcon={
                        <Icon
                        name='feature-search-outline'
                        size={24}
                        color='black'
                        />
                    }
                />
            </View>
                
                <Input
                    placeholder='Quantity'
                    leftIcon={
                        <Icon
                        name='list-number'
                        size={24}
                        color='black'
                        />
                    }
                />
                <View style={styles.datePicker}>
                    <Text>From:</Text>
                    <DatePicker
                        style={{ width: 210 }}
                        date={date}
                        mode="date"
                        placeholder="select date"
                        format="DD-MM-YYYY"
                        minDate={date}
                        confirmBtnText="Confirm"
                        cancelBtnText="Cancel"
                        customStyles={{
                        dateIcon: {
                            position: "absolute",
                            left: 0,
                            top: 4,
                            marginLeft: 0,
                        },
                        dateInput: {
                            marginLeft: 36,
                        },
                        // ... You can check the source to find the other keys.
                        }}
                        onDateChange={(date) => {setDate({date})}}
                    />
                    </View>
                    <View style={styles.datePicker2}>
                    <Text>To:</Text>
                    <DatePicker
                        style={{ width: 210 }}
                        date={date}
                        mode="date"
                        placeholder="select date"
                        format="DD-MM-YYYY"
                        minDate={date}
                        confirmBtnText="Confirm"
                        cancelBtnText="Cancel"
                        customStyles={{
                        dateIcon: {
                            position: "absolute",
                            left: 0,
                            top: 4,
                            marginLeft: 0,
                        },
                        dateInput: {
                            marginLeft: 36,
                        },
                        // ... You can check the source to find the other keys.
                        }}
                        onDateChange={(date) => {setDate({date: date})}}
                    />
                    </View>

                <Input
                placeholder='Rates/Day/Week/Month'
                leftIcon={{ type: 'font-awesome', name: 'dollar' }}
                />
            </View>
            <Button title="Add Photots"  type = "file"/>
            <Button title="Add Equipment"  type = "file"/>
            
            {/* <FormLabel>Name</FormLabel>
            <FormInput />
            <FormValidationMessage>Error message</FormValidationMessage> */}
        </View>
    )
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        flexDirection:'column',
        alignItems:'center',
        justifyContent:'center',

    },
    button:{
        margin:15,
        width:200,
        
    },
    Input : {
        width : 400,
        margin : 5
    },
    datePicker: {
        margin: 26,
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center",
      },
      datePicker2: {
        marginLeft: 14,
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center",
      },
      field : {
        marginLeft: 14,
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center",
      }
     
})
export default AddEquipment;
