import { FormLabel, FormInput, FormValidationMessage } from 'react-native-elements'
import { StyleSheet, Text, View, Button } from 'react-native'
import React , {useState } from 'react'
import { Avatar } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';
import { Input } from 'react-native-elements';
import DatePicker from "react-native-datepicker";

import DateTimePickerModal from "react-native-modal-datetime-picker";
// import MenuDrawer from '../routes/MenuDrawer';


const AddEquipment = ({navigation})=>{
    localStorage.setItem("subscription", true);
    // navigation.navigate('MenuDrawer')
    return (
        <View style={styles.container}>
            <View style = {styles.Input} >
            <Input
                placeholder='Equipment Name'
                leftIcon={{ type: 'font-awesome', name: 'id-badge' }}
                />
                <Input
                    placeholder=''
                    leftIcon={
                        <Icon
                        name='address-card-o'
                        size={24}
                        color='black'
                        />
                    }
                />
                <View style={styles.datePicker}>
                    <Text>From:</Text>
                    <DatePicker
                        style={{ width: 210 }}
                        date={date}
                        mode="date"
                        placeholder="select date"
                        format="DD-MM-YYYY"
                        minDate={date}
                        confirmBtnText="Confirm"
                        cancelBtnText="Cancel"
                        customStyles={{
                        dateIcon: {
                            position: "absolute",
                            left: 0,
                            top: 4,
                            marginLeft: 0,
                        },
                        dateInput: {
                            marginLeft: 36,
                        },
                        // ... You can check the source to find the other keys.
                        }}
                        // onDateChange={(date) => {setDate({date})}}
                    />
                    </View>
                    <View style={styles.datePicker2}>
                    <Text>To:</Text>
                    <DatePicker
                        style={{ width: 210 }}
                        date={date}
                        mode="date"
                        placeholder="select date"
                        format="DD-MM-YYYY"
                        minDate={date}
                        confirmBtnText="Confirm"
                        cancelBtnText="Cancel"
                        customStyles={{
                        dateIcon: {
                            position: "absolute",
                            left: 0,
                            top: 4,
                            marginLeft: 0,
                        },
                        dateInput: {
                            marginLeft: 36,
                        },
                        // ... You can check the source to find the other keys.
                        }}
                        // onDateChange={(date) => {setDate({date: date})}}
                    />
                    </View>

                <Input
                placeholder='Contact Details'
                disabled = "true"
                value = "+9079946155"
                leftIcon={{ type: 'font-awesome', name: 'mobile-phone' }}
                />
            </View>
                <Button
                    title="Update Info"
                />
            
            {/* <FormLabel>Name</FormLabel>
            <FormInput />
            <FormValidationMessage>Error message</FormValidationMessage> */}
        </View>
    )
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        flexDirection:'column',
        alignItems:'center',
        justifyContent:'center',

    },
    button:{
        margin:15,
        width:200,
        
    },
    Input : {
        width : 300,
        margin : 5
    },
    datePicker: {
        margin: 26,
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center",
      },
      datePicker2: {
        marginLeft: 14,
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center",
      },
     
})
export default AddEquipment;
